package pkg;

public class Datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte a=23;
		System.out.println("Byte value of a = "+ a);
		short b=20;
		System.out.println("Short value of b = "+ b);
		long c=2368999999l;
		System.out.println("Long value of c = "+ c);
		int i=500;
		System.out.println("Integer value of e = "+ i);
		float d=10.00f;
		System.out.println("Float value of d = "+ d);
		Double e=50.2245566;
		System.out.println("Double value of e = "+ e);
		char f='a';
		System.out.println("Character value of f = "+ f);
		char h=89;
		System.out.println("Character value of h = "+ h);
		boolean g=30>40;
		System.out.println("Boolean value of g = "+ g);
		String s="abc";
		System.out.println("String value = "+ s);
	}

}
